import { createContext, useContext } from 'react';

const RawTableContext = createContext({
    rowIndex: 0,
    colIndex: 0,
    setTableValues() {
        throw new Error('setTableValues must be initialized via the RawTableContext.Provider');
    },
});
const useTable = () => useContext(RawTableContext);

export { RawTableContext, useTable };
